p;&amp;nbsp;&amp;nbsp;&amp;nbsp;require_once&amp;nbsp;&amp;#39;AliPay.php&amp;#39;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$model=new&amp;nbsp;AliPay();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$data=[\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;subject&amp;#39;=&amp;gt;&amp;#39;测试订单&amp;#39;,//商品的标题\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;out_trade_no&amp;#39;=&amp;gt;time().uniqid(),//商户网站唯一订单号\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;total_amount&amp;#39;=&amp;gt;&amp;#39;100&amp;#39;,//订单总金额，单位为元\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;];\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$url=$model-&amp;gt;PcPay($data);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;header(&amp;quot;Location:&amp;nbsp;{$url}&amp;quot;);\r\n};\r\n\r\n\r\n//手机网站支付\r\nfunction&amp;nbsp;wap_pay(){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;require_once&amp;nbsp;&amp;#39;AliPay.php&amp;#39;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$model=new&amp;nbsp;AliPay();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$data=[\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;subject&amp;#39;=&amp;gt;&amp;#39;测试订单&amp;#39;,//商品的标题\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;out_trade_no&amp;#39;=&amp;gt;time().uniqid(),//商户网站唯一订单号\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;total_amount&amp;#39;=&amp;gt;&amp;#39;100&amp;#39;,//订单总金额，单位为元\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;];\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$url=$model-&amp;gt;WapPay($data);\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;header(&amp;quot;Location:&amp;nbsp;{$url}&amp;quot;);\r\n}\r\n\r\n//wap_pay();\r\npc_pay();&lt;/pre&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181002/1538457317170394.png&quot; title=&quot;1538457317170394.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;', '支付宝', '0', '1538457378', '2018-10-02 13:16:18', '37', '1', '1', '1');
INSERT INTO `ty_article` VALUES ('48', '1', '/uploads/article/20181102/5a63978ccdb840d44e3e26fc62fe863f.png', '5', 'PHP原生导出excel表格', '导出功能一般在后台用得比较多，如果只是简单导出，不需要样式的话没必要用第三方类库，直接原生导出，这样在数据量比较大的时候不至于卡死', 'admin', 'admin', '&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;多得不说，直接上代码：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;/**\r\n&amp;nbsp;*&amp;nbsp;@date&amp;nbsp;2018-10-8\r\n&amp;nbsp;*&amp;nbsp;demo:\r\n&amp;nbsp;*&amp;nbsp;$data=[\r\n&amp;nbsp;*&amp;nbsp;&amp;nbsp;&amp;nbsp;[0]=&amp;gt;[\r\n&amp;nbsp;*&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;phone&amp;#39;=&amp;gt;&amp;#39;123456&amp;#39;,\r\n&amp;nbsp;*&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;company&amp;#39;=&amp;gt;&amp;#39;ABC&amp;#39;\r\n&amp;nbsp;*&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;],\r\n&amp;nbsp;*&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[1]=&amp;gt;[\r\n&amp;nbsp;*&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;phone&amp;#39;=&amp;gt;&amp;#39;123456&amp;#39;,\r\n&amp;nbsp;*&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;company&amp;#39;=&amp;gt;&amp;#39;ABC&amp;#39;\r\n&amp;nbsp;*&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;]\r\n&amp;nbsp;*&amp;nbsp;]\r\n&amp;nbsp;*&amp;nbsp;$header=&amp;#39;电话，公司代码&amp;#39;;\r\n&amp;nbsp;*&amp;nbsp;$this-&amp;gt;OutExcel($data,$header)\r\n&amp;nbsp;*/\r\npublic&amp;nbsp;function&amp;nbsp;OutExcel(array&amp;nbsp;$data,$header=&amp;#39;&amp;#39;,$title=&amp;#39;表格&amp;#39;){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(!strlen($header))&amp;nbsp;return&amp;nbsp;false;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;header(&amp;quot;Content-type:application/vnd.ms-excel;charset=utf-8&amp;quot;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;header(&amp;quot;Content-Disposition:filename=&amp;quot;.urlencode($title).&amp;quot;.xls&amp;quot;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str=str_replace([&amp;#39;，&amp;#39;,&amp;#39;,&amp;#39;],[&amp;quot;\\t&amp;quot;,&amp;quot;\\t&amp;quot;],$header).&amp;quot;\\r&amp;quot;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;foreach($data&amp;nbsp;as&amp;nbsp;$vol){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(is_array($vol)){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;foreach($vol&amp;nbsp;as&amp;nbsp;$v){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str.=$v.(next($vol)?&amp;quot;\\t&amp;quot;:&amp;quot;\\r&amp;quot;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$str=iconv(&amp;#39;UTF-8&amp;#39;,&amp;quot;GB2312//IGNORE&amp;quot;,$str);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;exit($str);\r\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '导出', '0', '1538984779', '2018-10-08 15:46:19', '43', '1', '0', '1');
INSERT INTO `ty_article` VALUES ('49', '1', '/uploads/article/20181102/fd99f866f68865778bdef9ac4d95bca6.jpeg', '5', 'sphinx全文检索引擎', '模糊搜索在项目中应用到的场景非常多，一般小项目十几万，几十万的数据量采用like或instr()等方法模糊搜索还勉强可以应付，但是当数据量达到上百万，千万级的时候，如果还用上面的方式来查询的话就不现实了。', 'admin', 'admin', '&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;本人试了下在280万数据量的情况下用传统的instr()函数的方法去构建sql语句去搜索一个标题含有某个关键字的数据，用了接近20s，可想而知数据量增多时对模糊查询的影响有多大，下面简单说下利用sphinx来满足大数据量情况下的模糊搜索。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;以windows环境为例，首先下载sphinx，&lt;/span&gt;&lt;a href=&quot;http://www.sphinxsearch.com/downloads/&quot; target=&quot;_blank&quot; title=&quot;点我下载&quot; style=&quot;font-size: 14px; text-decoration: underline;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;点我下载&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;，我下载的是最新版3.0.3版本，下载完得到这样一个压缩文件&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181016/1539670148746152.png&quot; title=&quot;1539670148746152.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-indent: 2em;&quot;&gt;&lt;span style=&quot;text-indent: 2em; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 2em; font-size: 14px;&quot;&gt;将此压缩文件解压后，复制到D盘，命名为sphinx，方便等会测试，将etc里面的sphinx.conf.dist复制一份到bin目录下，&lt;/span&gt;&lt;span style=&quot;font-size: 14px; text-indent: 2em;&quot;&gt;并更名为sphinx.conf&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-indent: 2em;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181016/1539670582857211.png&quot; title=&quot;1539670582857211.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;将etc下面的example.sql导入到自己的数据库，注意此文件数据库名为test，所以不想麻烦的话新建一个test数据库来测试，&lt;/span&gt;&lt;span style=&quot;font-size: 14px; text-indent: 2em;&quot;&gt;搜索sql_host找到这块配置你的mysql信息&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181016/1539670369990260.png&quot; title=&quot;1539670369990260.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;搜索index test1，找到挨着下面的path配置索引目录，这里我是以相对路径设置的&lt;/span&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181016/1539670505436370.png&quot; title=&quot;1539670505436370.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;打开cmd窗口进入到sphinx下的bin目录，执行indexer.exe test1&amp;nbsp; 来建立索引，过程中如提示错误根据错误作相应调整，一般是某个文件夹没有创建，根据提示创建相应目录即可，然后执行searchd.exe启动服务，不要关闭cmd窗口&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;新建一个测试php文件，sphinxapi.php在sphinx目录下的api文件夹里面，可以自己复制出来，代码如下：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;br/&gt;require&amp;nbsp;&amp;#39;sphinxapi.php&amp;#39;;&lt;br/&gt;$s&amp;nbsp;=&amp;nbsp;new&amp;nbsp;SphinxClient();&lt;br/&gt;$s-&amp;gt;SetServer(&amp;#39;localhost&amp;#39;,&amp;nbsp;9312);&lt;br/&gt;$result&amp;nbsp;=&amp;nbsp;$s-&amp;gt;Query(&amp;#39;中文&amp;#39;,&amp;#39;*&amp;#39;);&lt;br/&gt;print_r($result);&lt;br/&gt;&lt;/p&gt;&lt;/pre&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;此处搜索的关键字是在配置文件中sql_query里面包含的字段里面去查找，假如现在为test这张表创建了索引文件，test表包含id,test,name三个字段，如果sql_query=select id,test from test,那么现在你想根据name来搜索是搜索不到数据的，因为创建索引文件的时候根本没把name考虑进去。&lt;/span&gt;&lt;/p&gt;', 'sphinx', '0', '1539671266', '2018-10-16 14:27:46', '30', '1', '0', '1');
INSERT INTO `ty_article` VALUES ('50', '1', '', '6', 'mysql百万级数据查询优化【转】', '对mysql查询的一些优化认识，感觉还不错', 'Mosan_', 'csdn', '&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;1.两种查询引擎查询速度（myIsam 引擎&lt;span style=&quot;white-space: pre;&quot;&gt;&lt;/span&gt;）&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;InnoDB 中不保存表的具体行数，也就是说，执行select count(*) from table时，InnoDB要扫描一遍整个表来计算有多少行。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;MyISAM只要简单的读出保存好的行数即可。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;注意的是，当count(*)语句包含 where条件时，两种表的操作有些不同，InnoDB类型的表用count(*)或者count(主键)，加上where col 条件。其中col列是表的主键之外的其他具有唯一约束索引的列。这样查询时速度会很快。就是可以避免全表扫描。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;总结：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;mysql 在300万条数据（myisam引擎）情况下使用 count(*) 进行数据总数查询包含条件（正确设置索引）运行时间正常。对于经常进行读取的数据我们建议使用myIsam引擎。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;2.百万数据下mysql分页问题&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;在开发过程中我们经常会使用分页，核心技术是使用limit进行数据的读取，在使用limit进行分页的测试过程中，得到以下数据：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;news&amp;nbsp;order&amp;nbsp;by&amp;nbsp;id&amp;nbsp;desc&amp;nbsp;limit&amp;nbsp;0,10\r\n耗时0.003秒\r\nselect&amp;nbsp;*&amp;nbsp;from&amp;nbsp;news&amp;nbsp;order&amp;nbsp;by&amp;nbsp;id&amp;nbsp;desc&amp;nbsp;limit&amp;nbsp;10000,10\r\n耗时0.058秒\r\nselect&amp;nbsp;*&amp;nbsp;from&amp;nbsp;news&amp;nbsp;order&amp;nbsp;by&amp;nbsp;id&amp;nbsp;desc&amp;nbsp;limit&amp;nbsp;100000,10&amp;nbsp;\r\n耗时0.575秒\r\nselect&amp;nbsp;*&amp;nbsp;from&amp;nbsp;news&amp;nbsp;order&amp;nbsp;by&amp;nbsp;id&amp;nbsp;desc&amp;nbsp;limit&amp;nbsp;1000000,10\r\n耗时7.28秒&lt;/pre&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;我们惊讶的发现mysql在数据量大的情况下分页起点越大查询速度越慢，100万条起的查询速度已经需要7秒钟。这是一个我们无法接受的数值！&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;改进方案 1&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;news&amp;nbsp;\r\nwhere&amp;nbsp;id&amp;nbsp;&amp;gt;&amp;nbsp;&amp;nbsp;(select&amp;nbsp;id&amp;nbsp;from&amp;nbsp;news&amp;nbsp;order&amp;nbsp;by&amp;nbsp;id&amp;nbsp;desc&amp;nbsp;&amp;nbsp;limit&amp;nbsp;1000000,&amp;nbsp;1)\r\norder&amp;nbsp;by&amp;nbsp;id&amp;nbsp;desc&amp;nbsp;\r\nlimit&amp;nbsp;0,10&lt;/pre&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;查询时间 0.365秒，提升效率是非常明显的！！原理是什么呢？？？&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;我们使用条件对id进行了筛选，在子查询 (select id from news order by id desc limit 1000000, 1) 中我们只查询了id这一个字段比起select * 或 select 多个字段 节省了大量的查询开销！&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;改进方案2&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;适合id连续的系统，速度极快！&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;news&amp;nbsp;\r\nwhere&amp;nbsp;id&amp;nbsp;&amp;nbsp;between&amp;nbsp;1000000&amp;nbsp;and&amp;nbsp;1000010&amp;nbsp;\r\norder&amp;nbsp;by&amp;nbsp;id&amp;nbsp;desc&lt;/pre&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;不适合带有条件的、id不连续的查询。速度非常快！&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;3.&lt;span style=&quot;font-size: 14px; white-space: pre;&quot;&gt;&lt;/span&gt;百万数据下mysql条件查询、分页查询的注意事项&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;接上一节，我们加上查询条件：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;select&amp;nbsp;id&amp;nbsp;from&amp;nbsp;news&amp;nbsp;\r\nwhere&amp;nbsp;cate&amp;nbsp;=&amp;nbsp;1\r\norder&amp;nbsp;by&amp;nbsp;id&amp;nbsp;desc&amp;nbsp;\r\nlimit&amp;nbsp;500000&amp;nbsp;,10&lt;/pre&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;查询时间 20 秒&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;好恐怖的速度！！利用第一节知识进行优化：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;news\r\nwhere&amp;nbsp;cate&amp;nbsp;=&amp;nbsp;1&amp;nbsp;and&amp;nbsp;id&amp;nbsp;&amp;gt;&amp;nbsp;(select&amp;nbsp;id&amp;nbsp;from&amp;nbsp;news&amp;nbsp;where&amp;nbsp;cate&amp;nbsp;=&amp;nbsp;1&amp;nbsp;order&amp;nbsp;by&amp;nbsp;id&amp;nbsp;desc&amp;nbsp;limit&amp;nbsp;500000,1&amp;nbsp;)&amp;nbsp;\r\norder&amp;nbsp;by&amp;nbsp;id&amp;nbsp;desc&amp;nbsp;\r\nlimit&amp;nbsp;0,10&lt;/pre&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;查询时间 15 秒&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;优化效果不明显，条件带来的影响还是很大！在这样的情况下无论我们怎么去优化sql语句就无法解决运行效率问题。那么换个思路：建立一个索引表，只记录文章的id、分类信息，我们将文章内容这个大字段分割出去。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;表 news2 [ 文章表 引擎 myisam 字符集 utf-8 ]&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;-------------------------------------------------&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;id&lt;span style=&quot;font-size: 14px; white-space: pre;&quot;&gt;&lt;/span&gt;int&lt;span style=&quot;font-size: 14px; white-space: pre;&quot;&gt;&lt;/span&gt;11&lt;span style=&quot;font-size: 14px; white-space: pre;&quot;&gt;&lt;/span&gt;主键自动增加&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;cate&lt;span style=&quot;font-size: 14px; white-space: pre;&quot;&gt;&lt;/span&gt;int&lt;span style=&quot;font-size: 14px; white-space: pre;&quot;&gt;&lt;/span&gt;11&lt;span style=&quot;font-size: 14px; white-space: pre;&quot;&gt;&lt;/span&gt;索引&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;在写入数据时将2张表同步，查询是则可以使用news2 来进行条件查询：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;news\r\nwhere&amp;nbsp;cate&amp;nbsp;=&amp;nbsp;1&amp;nbsp;and&amp;nbsp;id&amp;nbsp;&amp;gt;&amp;nbsp;(select&amp;nbsp;id&amp;nbsp;from&amp;nbsp;news2&amp;nbsp;where&amp;nbsp;cate&amp;nbsp;=&amp;nbsp;1&amp;nbsp;order&amp;nbsp;by&amp;nbsp;id&amp;nbsp;desc&amp;nbsp;limit&amp;nbsp;500000,1&amp;nbsp;)&amp;nbsp;\r\norder&amp;nbsp;by&amp;nbsp;id&amp;nbsp;desc&amp;nbsp;\r\nlimit&amp;nbsp;0,10&lt;/pre&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;注意条件 id &amp;gt; 后面使用了news2 这张表！&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;运行时间 1.23秒，我们可以看到运行时间缩减了近20倍！！数据在10万左右是查询时间可以保持在0.5秒左右，是一个逐步接近我们能够容忍的值！&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;但是1秒对于服务器来说依然是一个不能接受的值！！还有什么可以优化的办法吗？？我们尝试了一个伟大的变化：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;将 news2 的存储引擎改变为innodb，执行结果是惊人的！&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;news\r\nwhere&amp;nbsp;cate&amp;nbsp;=&amp;nbsp;1&amp;nbsp;and&amp;nbsp;id&amp;nbsp;&amp;gt;&amp;nbsp;(select&amp;nbsp;id&amp;nbsp;from&amp;nbsp;news2&amp;nbsp;where&amp;nbsp;cate&amp;nbsp;=&amp;nbsp;1&amp;nbsp;order&amp;nbsp;by&amp;nbsp;id&amp;nbsp;desc&amp;nbsp;limit&amp;nbsp;500000,1&amp;nbsp;)&amp;nbsp;\r\norder&amp;nbsp;by&amp;nbsp;id&amp;nbsp;desc&amp;nbsp;\r\nlimit&amp;nbsp;0,10&lt;/pre&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;只需要 0.2秒，非常棒的速度。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;4.mysql存储引擎 myIsam和innodb的区别&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;MySQL有多种存储引擎，MyISAM和InnoDB是其中常用的两种。这里介绍关于这两种引擎的一些基本概念（非深入介绍）。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;MyISAM存储引擎，基于传统的ISAM类型，支持全文搜索，但不是事务安全的，而且不支持外键。每张MyISAM表存放在三个文件中：frm 文件存放表格定义；数据文件是MYD (MYData)；索引文件是MYI (MYIndex)。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;InnoDB是事务型引擎，支持回滚、崩溃恢复能力、多版本并发控制、ACID事务，支持行级锁定（InnoDB表的行锁不是绝对的，如果在执行一个SQL语句时MySQL不能确定要扫描的范围，InnoDB表同样会锁全表，如like操作时的SQL语句），以及提供与Oracle类型一致的不加锁读取方式。InnoDB存储它的表和索引在一个表空间中，表空间可以包含数个文件。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;&lt;strong&gt;核心区别&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;MyISAM是非事务安全型的，而InnoDB是事务安全型的。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;MyISAM锁的粒度是表级，而InnoDB支持行级锁定。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;MyISAM支持全文类型索引，而InnoDB不支持全文索引。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;MyISAM相对简单，所以在效率上要优于InnoDB，小型应用可以考虑使用MyISAM。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;MyISAM表是保存成文件的形式，在跨平台的数据转移中使用MyISAM存储会省去不少的麻烦。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;InnoDB表比MyISAM表更安全，可以在保证数据不会丢失的情况下，切换非事务表到事务表（alter table tablename type=innodb）。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;&lt;strong&gt;应用场景&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;MyISAM管理非事务表。它提供高速存储和检索，以及全文搜索能力。如果应用中需要执行大量的SELECT查询，那么MyISAM是更好的选择。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;InnoDB用于事务处理应用程序，具有众多特性，包括ACID事务支持。如果应用中需要执行大量的INSERT或UPDATE操作，则应该使用InnoDB，这样可以提高多用户并发操作的性能。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;&lt;strong&gt;Mysql的存储引擎和索引&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;数据库必须有索引，没有索引则检索过程变成了顺序查找，O(n)的时间复杂度几乎是不能忍受的。我们非常容易想象出一个只有单关键字组成的表如何使用B+树进行索引，只要将关键字存储到树的节点即可。当数据库一条记录里包含多个字段时，一棵B+树就只能存储主键，如果检索的是非主键字段，则主键索引失去作用，又变成顺序查找了。这时应该在第二个要检索的列上建立第二套索引。 这个索引由独立的B+树来组织。有两种常见的方法可以解决多个B+树访问同一套表数据的问题，一种叫做聚簇索引（clustered index ），一种叫做非聚簇索引（secondary index）。这两个名字虽然都叫做索引，但这并不是一种单独的索引类型，而是一种数据存储方式。对于聚簇索引存储来说，行数据和主键B+树存储在一起，辅助键B+树只存储辅助键和主键，主键和非主键B+树几乎是两种类型的树。对于非聚簇索引存储来说，主键B+树在叶子节点存储指向真正数据行的指针，而非主键。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;InnoDB使用的是聚簇索引，将主键组织到一棵B+树中，而行数据就储存在叶子节点上，若使用&amp;quot;where id = 14&amp;quot;这样的条件查找主键，则按照B+树的检索算法即可查找到对应的叶节点，之后获得行数据。若对Name列进行条件搜索，则需要两个步骤：第一步在辅助索引B+树中检索Name，到达其叶子节点获取对应的主键。第二步使用主键在主索引B+树种再执行一次B+树检索操作，最终到达叶子节点即可获取整行数据。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;MyISM使用的是非聚簇索引，非聚簇索引的两棵B+树看上去没什么不同，节点的结构完全一致只是存储的内容不同而已，主键索引B+树的节点存储了主键，辅助键索引B+树存储了辅助键。表数据存储在独立的地方，这两颗B+树的叶子节点都使用一个地址指向真正的表数据，对于表数据来说，这两个键没有任何差别。由于索引树是独立的，通过辅助键检索无需访问主键的索引树。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;为了更形象说明这两种索引的区别，我们假想一个表如下图存储了4行数据。其中Id作为主索引，Name作为辅助索引。图示清晰的显示了聚簇索引和非聚簇索引的差异。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;我们重点关注聚簇索引，看上去聚簇索引的效率明显要低于非聚簇索引，因为每次使用辅助索引检索都要经过两次B+树查找，这不是多此一举吗？聚簇索引的优势在哪？&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;1 由于行数据和叶子节点存储在一起，这样主键和行数据是一起被载入内存的，找到叶子节点就可以立刻将行数据返回了，如果按照主键Id来组织数据，获得数据更快。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;2 辅助索引使用主键作为&amp;quot;指针&amp;quot; 而不是使用地址值作为指针的好处是，减少了当出现行移动或者数据页分裂时辅助索引的维护工作，使用主键值当作指针会让辅助索引占用更多的空间，换来的好处是InnoDB在移动行时无须更新辅助索引中的这个&amp;quot;指针&amp;quot;。也就是说行的位置（实现中通过16K的Page来定位，后面会涉及）会随着数据库里数据的修改而发生变化（前面的B+树节点分裂以及Page的分裂），使用聚簇索引就可以保证不管这个主键B+树的节点如何变化，辅助索引树都不受影响。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;所以在百万级数据及更大数据情况下，mysql innoDB 的索引表现更加优秀！&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;&lt;strong&gt;5、MySQL性能优化的一些经验&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;a.为查询优化你的查询&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;大多数的MySQL服务器都开启了查询缓存。这是提高性能最有效的方法之一，而且这是被MySQL的数据库引擎处理的。当有很多相同的查询被执行了多次的时候，这些查询结果会被放到一个缓存中，这样，后续的相同的查询就不用操作表而直接访问缓存结果了。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;这里最主要的问题是，对于程序员来说，这个事情是很容易被忽略的。因为，我们某些查询语句会让MySQL不使用缓存。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;请看下面的示例：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;// 查询缓存不开启&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;$r&amp;nbsp;=&amp;nbsp;mysql_query(&amp;quot;SELECT&amp;nbsp;username&amp;nbsp;FROM&amp;nbsp;user&amp;nbsp;WHERE&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;signup_date&amp;nbsp;&amp;gt;=&amp;nbsp;CURDATE()&amp;quot;);&lt;/pre&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;// 开启查询缓存&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;$today&amp;nbsp;=&amp;nbsp;date(&amp;quot;Y-m-d&amp;quot;);\r\n$r&amp;nbsp;=&amp;nbsp;mysql_query(&amp;quot;SELECT&amp;nbsp;username&amp;nbsp;FROM&amp;nbsp;user&amp;nbsp;WHERE&amp;nbsp;signup_date&amp;nbsp;&amp;gt;=&amp;nbsp;&amp;#39;$today&amp;#39;&amp;quot;);&lt;/pre&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;上面两条SQL语句的差别就是 CURDATE() ，MySQL的查询缓存对这个函数不起作用。所以，像 NOW() 和 RAND() 或是其它的诸如此类的SQL函数都不会开启查询缓存，因为这些函数的返回是会不定的易变的。所以，你所需要的就是用一个变量来代替MySQL的函数，从而开启缓存。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;b.学会使用EXPLAIN&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;使用EXPLAIN关键字可以让你知道MySQL是如何处理你的SQL语句的。&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;select&amp;nbsp;id,&amp;nbsp;title,&amp;nbsp;cate&amp;nbsp;from&amp;nbsp;news&amp;nbsp;where&amp;nbsp;cate&amp;nbsp;=&amp;nbsp;1&lt;br/&gt;&lt;/span&gt;&lt;/pre&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;发现查询缓慢，然后在cate字段上增加索引，则会加快查询&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;c.当只要一行数据时使用LIMIT 1&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;当你查询表的有些时候只需要一条数据，请使用 limit 1。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;d.正确的使用索引&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;索引并不一定就是给主键或是唯一的字段。如果在你的表中，有某个字段你总要会经常用来做搜索、拍下、条件，那么，请为其建立索引吧。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;e.不要ORDER BY RAND()&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;效率很低的一种随机查询。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;f.避免SELECT *&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;从数据库里读出越多的数据，那么查询就会变得越慢。并且，如果你的数据库服务器和WEB服务器是两台独立的服务器的话，这还会增加网络传输的负载。必须应该养成一个需要什么就取什么的好的习惯。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;g.使用 ENUM 而不是 VARCHAR&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;ENUM 类型是非常快和紧凑的。在实际上，其保存的是 TINYINT，但其外表上显示为字符串。这样一来，用这个字段来做一些选项列表变得相当的完美。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;如果你有一个字段，比如“性别”，“国家”，“民族”，“状态”或“部门”，你知道这些字段的取值是有限而且固定的，那么，你应该使用 ENUM 而不是 VARCHAR。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;h.使用 NOT NULL&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;除非你有一个很特别的原因去使用 NULL 值，你应该总是让你的字段保持 NOT NULL。这看起来好像有点争议，请往下看。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;首先，问问你自己“Empty”和“NULL”有多大的区别（如果是INT，那就是0和NULL）？如果你觉得它们之间没有什么区别，那么你就不要使用NULL。（你知道吗？在 Oracle 里，NULL 和 Empty 的字符串是一样的！)&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;不要以为 NULL 不需要空间，其需要额外的空间，并且，在你进行比较的时候，你的程序会更复杂。 当然，这里并不是说你就不能使用NULL了，现实情况是很复杂的，依然会有些情况下，你需要使用NULL值。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;下面摘自MySQL自己的文档&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;“NULL columns require additional space in the row to record whether their values are NULL. For MyISAM tables, each NULL column takes one bit extra, rounded up to the nearest byte.”&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;i.IP地址存成 UNSIGNED INT&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;很多程序员都会创建一个 VARCHAR(15) 字段来存放字符串形式的IP而不是整形的IP。如果你用整形来存放，只需要4个字节，并且你可以有定长的字段。而且，这会为你带来查询上的优势，尤其是当你需要使用这样的WHERE条件：IP between ip1 and ip2。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;我们必需要使用UNSIGNED INT，因为 IP地址会使用整个32位的无符号整形&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;j.固定长度的表会更快&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;如果表中的所有字段都是“固定长度”的，整个表会被认为是 “static” 或 “fixed-length”。 例如，表中没有如下类型的字段： VARCHAR，TEXT，BLOB。只要你包括了其中一个这些字段，那么这个表就不是“固定长度静态表”了，这样，MySQL 引擎会用另一种方法来处理。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;固定长度的表会提高性能，因为MySQL搜寻得会更快一些，因为这些固定的长度是很容易计算下一个数据的偏移量的，所以读取的自然也会很快。而如果字段不是定长的，那么，每一次要找下一条的话，需要程序找到主键。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;并且，固定长度的表也更容易被缓存和重建。不过，唯一的副作用是，固定长度的字段会浪费一些空间，因为定长的字段无论你用不用，他都是要分配那么多的空间。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;k.垂直分割&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;“垂直分割”是一种把数据库中的表按列变成几张表的方法，这样可以降低表的复杂度和字段的数目，从而达到优化的目的。需要注意的是，这些被分出去的字段所形成的表，你不会经常性地去Join他们，不然的话，这样的性能会比不分割时还要差，而且，会是极数级的下降。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;l.拆分大的 DELETE 或 INSERT 语句&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;如果在一个在线的网站上去执行一个大的 DELETE 或 INSERT 查询，你需要非常小心，要避免你的操作让你的整个网站停止相应。因为这两个操作是会锁表的，表一锁住了，别的操作都进不来了。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;Apache 会有很多的子进程或线程。所以，其工作起来相当有效率，而我们的服务器也不希望有太多的子进程，线程和数据库链接，这是极大的占服务器资源的事情，尤其是内存。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;如果你把你的表锁上一段时间，比如30秒钟，那么对于一个有很高访问量的站点来说，这30秒所积累的访问进程/线程，数据库链接，打开的文件数，可能不仅仅会让你泊WEB服务Crash，还可能会让你的整台服务器马上掛了。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;m.越小的列会越快&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;对于大多数的数据库引擎来说，硬盘操作可能是最重大的瓶颈。所以，把你的数据变得紧凑会对这种情况非常有帮助，因为这减少了对硬盘的访问。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;n.选择正确的存储引擎&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;在 MySQL 中有两个存储引擎 MyISAM 和 InnoDB，每个引擎都有利有弊。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;MyISAM 适合于一些需要大量查询的应用，但其对于有大量写操作并不是很好。甚至你只是需要update一个字段，整个表都会被锁起来，而别的进程，就算是读进程都无法操作直到读操作完成。另外，MyISAM 对于 SELECT COUNT(*) 这类的计算是超快无比的。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;InnoDB 的趋势会是一个非常复杂的存储引擎，对于一些小的应用，它会比 MyISAM 还慢。他是它支持“行锁” ，于是在写操作比较多的时候，会更优秀。并且，他还支持更多的高级应用，比如：事务。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;---------------------&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;作者：Mosan_&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;来源：CSDN&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;原文：https://blog.csdn.net/qq_35472110/article/details/80832304?utm_source=copy&amp;nbsp;&lt;/span&gt;&lt;/p&gt;', 'mysql', '0', '1539741509', '2018-10-17 09:58:29', '12', '1', '0', '0');
INSERT INTO `ty_article` VALUES ('51', '1', '/uploads/article/20181102/5e83a097473013726ac3cef169489ba1.jpg', '9', 'mysql启用skip-name-resolve模式时出现Warning的处理办法', 'skip-name-resolve 参数的目的是不再进行反解析（ip不反解成域名），这样可以加快数据库的反应时间。', '网络', '网络', '&lt;p&gt;修改配置文件添加并需要重启：&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;[mysqld]&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;skip-name-resolve&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;添加后发现错误日志有警告信息&lt;/p&gt;&lt;p&gt;131127 11:09:12 [Warning] &amp;#39;user&amp;#39; entry &amp;#39;root@localhost&amp;#39; ignored in --skip-name-resolve mode.&lt;/p&gt;&lt;p&gt;131127 11:09:12 [Warning] &amp;#39;user&amp;#39; entry &amp;#39;@localhost&amp;#39; ignored in --skip-name-resolve mode.&lt;/p&gt;&lt;p&gt;131127 11:09:12 [Warning] &amp;#39;proxies_priv&amp;#39; entry &amp;#39;@ root@localhost&amp;#39; ignored in --skip-name-resolve mode&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;启用后，在mysql的授权表中就不能使用主机名了，只能使用IP ，出现此警告是由于mysql.user表中已经存在有相关的帐号信息。 我们把它删除就好了。&lt;/p&gt;&lt;p&gt;mysql&amp;gt;use mysql;&amp;nbsp;&lt;/p&gt;&lt;p&gt;mysql&amp;gt; delete&amp;nbsp; from user where HOST=&amp;#39;localhost&amp;#39;;&amp;nbsp;&lt;/p&gt;&lt;p&gt;Query OK, 2 rows affected (0.00 sec)&amp;nbsp; &amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;然后删除表mysql.proxies_priv中和localhost类似与具体域名有关的行。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;重启MYSQL 。&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 'linux', '0', '1539964807', '2018-10-20 00:00:07', '12', '1', '0', '0');
INSERT INTO `ty_article` VALUES ('52', '1', '/uploads/article/20181026/92e9c82be11f74e57cfd8340a35c731f.jpg', '5', 'PHP设计模式--观察者模式', '观察者模式目前我能理解的就是降低程序的耦合，方便后期的代码维护，比如某个流程的业务逻辑比较复杂，处理的东西比较多的话，代码堆积在一起对于后期的维护非常困难，如果能把一些逻辑独立出来那就比较方便了，需要改哪一个就改哪一个，不用担心会影响其他业务', 'admin', 'admin', '&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;一个简单的例子：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;//&amp;nbsp;主题接口\r\ninterface&amp;nbsp;Subject{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;register(Observer&amp;nbsp;$observer);\r\n}\r\n//&amp;nbsp;观察者接口\r\ninterface&amp;nbsp;Observer{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;call_back($obj);\r\n}\r\n//&amp;nbsp;主题类--商品购买\r\nclass&amp;nbsp;Action&amp;nbsp;implements&amp;nbsp;Subject{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;$_observers=array();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;$user=&amp;#39;&amp;#39;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;register(Observer&amp;nbsp;$observer){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;_observers[]=$observer;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;buy(){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//购买逻辑处理\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;user=&amp;#39;测试&amp;#39;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//循环调用需要通知的处理类、比如购买成功短信通知用户\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;foreach&amp;nbsp;($this-&amp;gt;_observers&amp;nbsp;as&amp;nbsp;$observer)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$observer-&amp;gt;call_back($this);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n}\r\n\r\n\r\n//发送短信\r\nclass&amp;nbsp;Msg&amp;nbsp;implements&amp;nbsp;Observer{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;call_back($obj){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;$obj-&amp;gt;user.&amp;#39;你好，xxx商品已购买成功&amp;lt;hr/&amp;gt;&amp;#39;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n}\r\n//赠送积分\r\nclass&amp;nbsp;GiveIntegral&amp;nbsp;implements&amp;nbsp;Observer{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;call_back($obj){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;$obj-&amp;gt;user.&amp;#39;你好，系统赠送xxx积分&amp;lt;pre&amp;gt;&amp;#39;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n}\r\n\r\n\r\n\r\n//&amp;nbsp;应用实例\r\n$action=new&amp;nbsp;Action();\r\n$action-&amp;gt;register(new&amp;nbsp;Msg());\r\n$action-&amp;gt;register(new&amp;nbsp;GiveIntegral());\r\n$action-&amp;gt;buy();&lt;/pre&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181023/1540277263470114.png&quot; title=&quot;1540277263470114.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;', '观察者模式', '0', '1540277274', '2018-10-23 14:47:54', '24', '1', '0', '1');
INSERT INTO `ty_article` VALUES ('53', '1', '/uploads/article/20181027/979ae4ee15584f0968a6b4dd9d85d874.jpg', '8', 'jquery获取元素的所有宽高（包括内边距和外边距）', '对于页面元素根据鼠标滚动到一定程度将元素位置固定来说获取元素的一些高度属性非常重要，这里列出一些常用jq获取元素宽，高的一些方法', '网路', '网络', '&lt;p&gt;width() - 返回元素的宽度。&lt;/p&gt;&lt;p&gt;height() - 返回元素的高度。&lt;/p&gt;&lt;p&gt;innerWidth() 方法返回元素的宽度（包括内边距）。&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;innerHeight() 方法返回元素的高度（包括内边距）。&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;outerWidth() 方法返回元素的宽度（包括内边距和边框）。&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;&lt;p&gt;outerHeight() 方法返回元素的高度（包括内边距和边框）。&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;outerWidth(true) 方法返回元素的宽度（包括内边距、边框和外边距）&lt;/p&gt;&lt;p&gt;outerHeight(true) 方法返回元素的高度（包括内边距、边框和外边距）。&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;返回文档（HTML 文档）$(document).height()的高度&lt;/p&gt;&lt;p&gt;返回窗口（浏览器视口）$(window).height()的高度&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;获取元素到浏览器顶部的距离:$(&amp;#39;xxx&amp;#39;).offset().top&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '', '0', '1540564246', '2018-10-26 22:30:46', '22', '1', '0', '1');
INSERT INTO `ty_article` VALUES ('54', '1', '/uploads/article/20181031/6e8312b64d44cc30a98f2a3cb1733811.jpg', '5', '利用nginx实现负载均衡【转】', '简单说明利用nginx实现负载均衡的一篇文章', 'fengzp', '博客园', '&lt;h1 style=&quot;margin: 10px 0px; padding: 0px; font-size: 28px; line-height: 1.5; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, Verdana, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;/h1&gt;&lt;p&gt;&lt;strong&gt;1、安装nginx和tomcat&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;我这里是使用docker安装的。安装流程可参照 dockerfile&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;这里安装了两个tomcat，端口分别是42000和42001。第二个tomcat的首页随便加了些代码区分&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;strong&gt;2、nginx配置&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;#这里的域名要和下面proxy_pass的一样\r\nupstream&amp;nbsp;&amp;nbsp;fengzp.com&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;server&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;192.168.99.100:42000&amp;nbsp;&amp;nbsp;weight=1;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;server&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;192.168.99.100:42001&amp;nbsp;&amp;nbsp;weight=2;&amp;nbsp;&amp;nbsp;\r\n}&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n\r\nserver&amp;nbsp;{&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;listen&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;80;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;server_name&amp;nbsp;&amp;nbsp;192.168.99.100;&amp;nbsp;&amp;nbsp;\r\n\r\nlocation&amp;nbsp;/&amp;nbsp;{&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;proxy_pass&amp;nbsp;http://fengzp.com;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;proxy_redirect&amp;nbsp;default;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;error_page&amp;nbsp;&amp;nbsp;&amp;nbsp;500&amp;nbsp;502&amp;nbsp;503&amp;nbsp;504&amp;nbsp;&amp;nbsp;/50x.html;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;location&amp;nbsp;=&amp;nbsp;/50x.html&amp;nbsp;{&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;root&amp;nbsp;&amp;nbsp;&amp;nbsp;html;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;\r\n}&lt;/pre&gt;&lt;p&gt;&lt;strong&gt;3，测试&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin: 10px auto; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181031/1540964602574601.png&quot; title=&quot;1540964602574601.png&quot; alt=&quot;954438-20170223163248007-1327727883.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin: 10px auto; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181031/1540964607553056.png&quot; title=&quot;1540964607553056.png&quot; alt=&quot;954438-20170223163302460-482845563.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;刷新页面发现页面会发生变化，证明负载配置成功。因为我配的权重第二个是第一个的两倍，所以第二个出现的概率会是第一个的两倍。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;4、后续问题&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;如果关了tomcat1，再多次刷新页面，接下来出现的就会都是tomcat2的页面，但是时而快时而慢。这其中原因是当如果nginx将请求转发到tomcat2时，服务器会马上跳转成功，但是如果是转到tomcat1，因为tomcat1已经关闭了，所以会出现一段等待响应过程的过程，要等它失败后才会转到tomcat2。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;而这个等待响应的时间我们是可以配置的。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;这个时间由以下3个参数控制：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;proxy_connect_timeout：与服务器连接的超时时间，默认60s&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;fail_timeout：当该时间内服务器没响应，则认为服务器失效，默认10s&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;max_fails：允许连接失败次数，默认为1&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;等待时间 = proxy_connect_timeout + fail_timeout * max_fails&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin: 10px auto; padding: 0px; font-family: &amp;quot;Helvetica Neue&amp;quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181031/1540964614464251.png&quot; title=&quot;1540964614464251.png&quot; alt=&quot;954438-20170223171507351-2087031920.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;如果我这样配置的话，只需等待6秒就可以了。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5、负载均衡策略&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;1、轮询&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;这种是默认的策略，把每个请求按顺序逐一分配到不同的server，如果server挂掉，能自动剔除。&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;upstream&amp;nbsp;&amp;nbsp;fengzp.com&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;server&amp;nbsp;&amp;nbsp;&amp;nbsp;192.168.99.100:42000;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;server&amp;nbsp;&amp;nbsp;&amp;nbsp;192.168.99.100:42001;&amp;nbsp;&amp;nbsp;\r\n}&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;2、最少连接&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;把请求分配到连接数最少的server&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;upstream&amp;nbsp;&amp;nbsp;fengzp.com&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;least_conn;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;server&amp;nbsp;&amp;nbsp;&amp;nbsp;192.168.99.100:42000;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;server&amp;nbsp;&amp;nbsp;&amp;nbsp;192.168.99.100:42001;&amp;nbsp;&amp;nbsp;\r\n}&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;3、权重&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;使用weight来指定server访问比率，weight默认是1。以下配置会是server2访问的比例是server1的两倍。&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;upstream&amp;nbsp;&amp;nbsp;fengzp.com&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;server&amp;nbsp;&amp;nbsp;&amp;nbsp;192.168.99.100:42000&amp;nbsp;weight=1;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;server&amp;nbsp;&amp;nbsp;&amp;nbsp;192.168.99.100:42001&amp;nbsp;weight=2;&amp;nbsp;&amp;nbsp;\r\n}&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;4、ip_hash&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;每个请求会按照访问ip的hash值分配，这样同一客户端连续的Web请求都会被分发到同一server进行处理，可以解决session的问题。如果server挂掉，能自动剔除。ip_hash可以和weight结合使用。&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;upstream&amp;nbsp;&amp;nbsp;fengzp.com&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ip_hash;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;server&amp;nbsp;&amp;nbsp;&amp;nbsp;192.168.99.100:42000;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;server&amp;nbsp;&amp;nbsp;&amp;nbsp;192.168.99.100:42001;&amp;nbsp;&amp;nbsp;\r\n}&lt;/pre&gt;', 'nginx', '0', '1540964628', '2018-10-31 13:43:48', '26', '1', '0', '1');
INSERT INTO `ty_article` VALUES ('55', '1', '/uploads/article/20181105/e6e133a897db2c412ab5fbb7ffa4915e.jpg', '8', 'js倒计时', '一般js倒计时用于一些抢购或其他类似活动场景，而这些对时效性要求非常高的应用，是不能容忍时间差存在的，此时倒计时就不能依赖于客户端时间了，如果依赖客户端，用户就可能会自行修改客户端的时间来扰乱我们正常程序的流程', 'admin', 'admin', '&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;为了避免这一问题，倒计时的时间我们在服务器来计算，然后返回到客户端，客户端只做减秒处理，下面直接上代码&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;!DOCTYPE&amp;nbsp;html&amp;gt;\r\n&amp;lt;html&amp;nbsp;lang=&amp;quot;en&amp;quot;&amp;gt;\r\n&amp;lt;head&amp;gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;meta&amp;nbsp;charset=&amp;quot;UTF-8&amp;quot;&amp;gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;title&amp;gt;活动倒计时&amp;lt;/title&amp;gt;\r\n&amp;lt;/head&amp;gt;\r\n&amp;lt;body&amp;gt;\r\n&amp;lt;span&amp;nbsp;class=&amp;quot;time&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;\r\n&amp;lt;/body&amp;gt;\r\n&amp;lt;script&amp;nbsp;src=&amp;quot;./jquery-3.2.1.min.js&amp;quot;&amp;gt;&amp;lt;/script&amp;gt;\r\n&amp;lt;script&amp;gt;\r\nvar&amp;nbsp;time=&amp;nbsp;5;//从服务器获取总倒计时长秒数\r\nvar&amp;nbsp;day&amp;nbsp;=Math.floor(time/(3600*24));\r\nvar&amp;nbsp;hour&amp;nbsp;=Math.floor((time-3600*24*day)/3600);\r\nvar&amp;nbsp;minute&amp;nbsp;=&amp;nbsp;Math.floor((time-3600*24*day-hour*3600)/60);\r\nvar&amp;nbsp;seconds&amp;nbsp;=time-3600*24*day-hour*3600-minute*60;\r\n$(&amp;#39;.time&amp;#39;).html(&amp;#39;距离活动开始还有:&amp;#39;+day&amp;nbsp;+&amp;nbsp;&amp;quot;天&amp;quot;&amp;nbsp;+&amp;nbsp;hour&amp;nbsp;+&amp;nbsp;&amp;quot;小时&amp;quot;&amp;nbsp;+&amp;nbsp;minute&amp;nbsp;+&amp;nbsp;&amp;quot;分钟&amp;quot;&amp;nbsp;+&amp;nbsp;seconds&amp;nbsp;+&amp;nbsp;&amp;quot;秒&amp;quot;);\r\nvar&amp;nbsp;t&amp;nbsp;=&amp;nbsp;setInterval(function()&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;time--;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;var&amp;nbsp;day&amp;nbsp;=Math.floor(time/(3600*24));\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;var&amp;nbsp;hour&amp;nbsp;=Math.floor((time-3600*24*day)/3600);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;var&amp;nbsp;minute&amp;nbsp;=&amp;nbsp;Math.floor((time-3600*24*day-hour*3600)/60);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;var&amp;nbsp;seconds&amp;nbsp;=time-3600*24*day-hour*3600-minute*60;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$(&amp;#39;.time&amp;#39;).html(&amp;#39;距离活动开始还有:&amp;#39;+day&amp;nbsp;+&amp;nbsp;&amp;quot;天&amp;quot;&amp;nbsp;+&amp;nbsp;hour&amp;nbsp;+&amp;nbsp;&amp;quot;小时&amp;quot;&amp;nbsp;+&amp;nbsp;minute&amp;nbsp;+&amp;nbsp;&amp;quot;分钟&amp;quot;&amp;nbsp;+&amp;nbsp;seconds&amp;nbsp;+&amp;nbsp;&amp;quot;秒&amp;quot;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//console.log(day&amp;nbsp;+&amp;nbsp;&amp;quot;天&amp;quot;&amp;nbsp;+&amp;nbsp;hour&amp;nbsp;+&amp;nbsp;&amp;quot;小时&amp;quot;&amp;nbsp;+&amp;nbsp;minute&amp;nbsp;+&amp;nbsp;&amp;quot;分钟&amp;quot;&amp;nbsp;+&amp;nbsp;seconds&amp;nbsp;+&amp;nbsp;&amp;quot;秒&amp;quot;+&amp;#39;time:&amp;#39;+time);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(time&amp;nbsp;==&amp;nbsp;0)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$(&amp;#39;.time&amp;#39;).html(&amp;#39;活动开始&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;clearInterval(t);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n},&amp;nbsp;1000);\r\n&amp;lt;/script&amp;gt;\r\n&amp;lt;/html&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;欢迎大佬指点其他更好的方法&lt;/span&gt;&lt;/p&gt;', 'js', '0', '1541406045', '2018-11-05 16:20:45', '17', '1', '0', '0');
INSERT INTO `ty_article` VALUES ('56', '1', '/uploads/article/20181114/765ee5fff4f2be817829d6643ef5ffbe.jpg', '5', 'php利用workerman的定时器实现延时操作的一个思路', '延时操作个人目前接触最多的场景是用户下单后一定时间未支付自动取消订单，传统的做法是采用定时任务定时扫描数据库超时订单或者利用客户端的请求每次请求去检测一次是否有超时订单，这些方法我始终感觉不是很好，所以一直想办法在弄一个比较理想的方法，至少比上面这两个方法好一点的处理方式', 'admin', 'admin', '&lt;p&gt;今天没事就研究了一下这个东西，主要是利用workerman的定时器。所以首先得下载workerman然后引入到自己的项目，或者composer安装也可以，具体就不再多说了。先贴出代码&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;use&amp;nbsp;Workerman\\Worker;\r\nuse&amp;nbsp;\\Workerman\\Lib\\Timer;\r\nrequire_once&amp;nbsp;&amp;#39;./Workerman/Autoloader.php&amp;#39;;\r\n\r\n\r\n\r\n//&amp;nbsp;创建一个Worker监听2345端口，使用http协议通讯\r\n$http_worker&amp;nbsp;=&amp;nbsp;new&amp;nbsp;Worker(&amp;quot;http://0.0.0.0:2345&amp;quot;);\r\n\r\n\r\n\r\n\r\n//&amp;nbsp;启动4个进程对外提供服务\r\n$http_worker-&amp;gt;count&amp;nbsp;=&amp;nbsp;4;\r\n\r\n\r\n\r\n\r\n//检查订单是否超时\r\nfunction&amp;nbsp;check_order($order){\r\n	//这个方法用于检查该订单是否超时。。。。具体逻辑根据实际应用场景写，这里只记录是否进入这个方法\r\n\r\n	file_put_contents(&amp;#39;./log.txt&amp;#39;,&amp;#39;订单编号:&amp;#39;.$order.&amp;#39;&amp;nbsp;处理时间:&amp;#39;.date(&amp;#39;Y-m-d&amp;nbsp;H:i:s&amp;#39;).&amp;quot;\\n&amp;quot;,FILE_APPEND);\r\n}\r\n\r\n\r\n\r\n\r\n$http_worker-&amp;gt;onMessage&amp;nbsp;=&amp;nbsp;function($connection,&amp;nbsp;$data)\r\n{\r\n	$msg=&amp;#39;&amp;#39;;\r\n	\r\n	\r\n	//&amp;nbsp;向浏览器发送hello&amp;nbsp;world\r\n	if(isset($data[&amp;#39;get&amp;#39;][&amp;#39;order&amp;#39;]))&amp;nbsp;{\r\n		$msg=json_encode($data[&amp;#39;get&amp;#39;]);\r\n		\r\n		\r\n		//10秒后执行检查订单方法，最后一个参数传递false，表示只运行一次\r\n		//这里记录请求时间用于对比延时操作的效果\r\n		\r\n		file_put_contents(&amp;#39;./log.txt&amp;#39;,&amp;#39;订单编号:&amp;#39;.$data[&amp;#39;get&amp;#39;][&amp;#39;order&amp;#39;].&amp;#39;&amp;nbsp;请求时间:&amp;#39;.date(&amp;#39;Y-m-d&amp;nbsp;H:i:s&amp;#39;).&amp;quot;\\n&amp;quot;,FILE_APPEND);\r\n		\r\n		\r\n		Timer::add(10,&amp;nbsp;&amp;#39;check_order&amp;#39;,&amp;nbsp;array($data[&amp;#39;get&amp;#39;][&amp;#39;order&amp;#39;]),&amp;nbsp;false);\r\n	}\r\n	\r\n	\r\n	\r\n	$connection-&amp;gt;send(&amp;#39;请求成功:&amp;#39;.$msg);\r\n};\r\n\r\n//&amp;nbsp;运行worker\r\nWorker::runAll();&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;新建一个php文件，假设命名为index.php，将上面代码复制到该文件，注意里面require_once的路径根据自己目录做调整，然后打开cmd窗口，进入到index.php所在目录，执行命令:php index.php，正常应该会看到的结果应该是这样&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181114/1542181735937689.png&quot; title=&quot;1542181735937689.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;如果报错需要去看看workerman的文档，这时不要关闭该cmd窗口。这时我们浏览器去访问：&lt;a href=&quot;http://127.0.0.1:2345/?order=No1&quot;&gt;http://127.0.0.1:2345/?order=No1&lt;/a&gt; &lt;/p&gt;&lt;p&gt;和&lt;a href=&quot;http://127.0.0.1:2345/?order=No1&quot; style=&quot;white-space: normal;&quot;&gt;http://127.0.0.1:2345/?order=No&lt;/a&gt;2，然后查看log.txt的记录&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181114/1542182224403296.png&quot; title=&quot;1542182224403296.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;看来效果不错，达到了我预期的目标；思路代码里面的注释应该看了就懂了，如有更好的方法，希望多多分享&lt;/p&gt;', '0', '0', '1542182343', '2018-11-14 15:59:03', '8', '1', '1', '1');
INSERT INTO `ty_article` VALUES ('57', '1', '/uploads/article/20181121/9a3dcf7dabbc602ec44fe7371e0e1106.jpg', '6', 'mysql8.0+ 创建或插入数据时报错row size too large (&gt;8126)', '昨天在把外网数据库复制到内网的操作中，订单表一直提示这个错误，row size too large (&gt;8126)，字面意思是某行数据过大导致插入或创建失败', 'amdin', '网络', '&lt;p&gt;如图：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20181121/1542763227648457.png&quot; title=&quot;1542763227648457.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;百度很多都是千篇一律，没有解决到问题，还好有一个歪果小哥和我遇到的问题几乎一样而且最后解决方法同样也解决了我的问题。解决方法就是在my.ini配置里面加上这两行：&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;innodb_log_file_size&amp;nbsp;=&amp;nbsp;512M\r\ninnodb_strict_mode&amp;nbsp;=&amp;nbsp;0&lt;/pre&gt;&lt;p&gt;ok，完美解决，至于为什么暂时没有去深究。&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;https://stackoverflow.com/questions/25163614/error-1118-42000-row-size-too-large&quot; target=&quot;_blank&quot; title=&quot;原文地址&quot; style=&quot;color: rgb(0, 176, 240); text-decoration: underline;&quot;&gt;&lt;span style=&quot;color: rgb(0, 176, 240);&quot;&gt;&lt;em&gt;&lt;strong&gt;原文地址&lt;/strong&gt;&lt;/em&gt;&lt;/span&gt;&lt;/a&gt;&lt;br/&gt;&lt;/p&gt;', 'mysql', '0', '1542763470', '2018-11-21 09:24:30', '11', '1', '0', '1');
INSERT INTO `ty_article` VALUES ('59', '1', '/uploads/article/20181122/c64a9675a7827794246688acb90a3970.jpg', '9', 'linux svn创建版本库【转】', 'svn新建版本库', 'W·Axes', 'cnblog', '&lt;p&gt;【新建svn仓库】&lt;/p&gt;&lt;p&gt;先创建一个文件夹mkdir /opt/svn/wechat;&amp;nbsp; &amp;nbsp;然后创建svn版本库&amp;nbsp; &amp;nbsp; svnadmin create /opt/svn/wechat;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;【创建用户组权限】&lt;/p&gt;&lt;p&gt;创建好后，创建用户组权限：打开/opt/svn/wechat/conf/authz文件，加上&lt;/p&gt;&lt;p&gt;[grounps]&lt;/p&gt;&lt;p&gt;team = xman,superman&lt;/p&gt;&lt;p&gt;[/]&amp;nbsp; (代表该组在该目录下所有文件都具有读写权限)&lt;/p&gt;&lt;p&gt;@team = rw&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;如果需要配置某个文件夹里不同权限：则&lt;/p&gt;&lt;p&gt;[/MaiKing/]&amp;nbsp;&lt;/p&gt;&lt;p&gt;superman = r&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;【创建用户名和密码】&lt;/p&gt;&lt;p&gt;然后创建用户名和密码，打开/opt/svn/wechat/conf/passwd文件，加上&lt;/p&gt;&lt;p&gt;xman= xman&lt;/p&gt;&lt;p&gt;superman= superman&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;【配置文件】&lt;/p&gt;&lt;p&gt;用户创建完毕，然后再打开/opt/svn/wechat/conf/svnserve.conf&lt;/p&gt;&lt;p&gt;将文件中general下的键值对的注释去掉&lt;/p&gt;&lt;p&gt;包括：&lt;/p&gt;&lt;p&gt;anon-access = read&lt;/p&gt;&lt;p&gt;auth-access = write&lt;/p&gt;&lt;p&gt;password-db = password&lt;/p&gt;&lt;p&gt;authz-db = authz&lt;/p&gt;&lt;p&gt;realm = My First .....&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;【checkout到项目目录】&lt;/p&gt;&lt;p&gt;然后checkout到项目根目录,先打开根目录&lt;/p&gt;&lt;p&gt;然后checkout版本库&amp;nbsp; &amp;nbsp;svn co svn://localhost:9999/wechat&lt;/p&gt;&lt;p&gt;然后输入服务器密码，输入svn账号，即可成功checkout&lt;/p&gt;&lt;p&gt;checkout完后wechat文件就是我们的项目目录啦&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;【创建hook】&lt;/p&gt;&lt;p&gt;然后创建hook，以便本地提交自动更新到项目根目录&lt;/p&gt;&lt;p&gt;vi /opt/svn/wechat/hooks/post-commit&lt;/p&gt;&lt;p&gt;然后输入：&lt;/p&gt;&lt;p&gt;#!/bin/sh&lt;/p&gt;&lt;p&gt;export.UTF-8 #svn 编码是UTF-8&lt;/p&gt;&lt;p&gt;/usr/local/svn/bin/svn up --username=xman --password=xman --no-auth-cache --non-interactive /alidata/www/wechat&lt;/p&gt;&lt;p&gt;然后wq保存。&lt;/p&gt;&lt;p&gt;保存好后再给post-commit这个hook文件设置权限：chmod 744 post-commit&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;然后就完事了&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 'svn', '0', '1542876188', '2018-11-22 16:43:08', '5', '1', '0', '0');
